        )
        response = cast(list[MonotonicLlmPlan], response)

        new_nodes: list[Node[PlanStep, GoalTestT]] = []

        for llm_plan in response:
            # We need to check if the step is satisfiable.
            proposed_step_raw = llm_plan.steps[-1]
